	case 'n':
	    opt_max_examples = optarg ? atoi(optarg) : 10;
	    if(opt_verbose || opt_debug) printf ("maximal number of examples to be produced: %d\n", opt_max_examples);
	    break;
	case 'k':
	    if(opt_debug) printf ("check completeness of LGF grammar: on\n");
